import React, { useState } from "react";
import "./App.css";



function App() {
  const [showFinalResults, setFinalResults] = useState();
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);


  const questions = [
    {
      text: "სად მდებარეობს 47-ე საჯარო სკოლა?",
      options: [
        { id: 0, text: "A) აი ოპერასთან რო აუხვევ", isCorrect: false },
        { id: 1, text: "ჩიტაძეზე", isCorrect: true },
        { id: 2, text: "სოლოლაკში", isCorrect: false },
        { id: 3, text: "მთაწმინდაზე", isCorrect: false },
      ],
    },
    {
      text: "რა ჰქვია მარტორქის შვილს?",
      options: [

        { id: 1, text: "კოზაკი", isCorrect: false },
        { id: 2, text: "კნუტი", isCorrect: false },
        { id: 3, text: "მარტორქოვიჩი", isCorrect: false },
        { id: 0, text: "დაგუგლე", isCorrect: true },
      ], 
    },
    {
      text: "",
      options: [
        { id: 0, text: "John Adams", isCorrect: true },
        { id: 1, text: "Paul Revere", isCorrect: false },
        { id: 2, text: "Thomas Jefferson", isCorrect: false },
        { id: 3, text: "Benjamin Franklin", isCorrect: false },
      ],
    },
    {
      text: "What is the largest state in the US?",
      options: [
        { id: 0, text: "California", isCorrect: false },
        { id: 1, text: "Alaska", isCorrect: true },
        { id: 2, text: "Texas", isCorrect: false },
        { id: 3, text: "Montana", isCorrect: false },
      ],
    },
    {
      text: "Which of the following countries DO NOT border the US?",
      options: [
        { id: 0, text: "Canada", isCorrect: false },
        { id: 1, text: "Russia", isCorrect: true },
        { id: 2, text: "Cuba", isCorrect: true },
        { id: 3, text: "Mexico", isCorrect: false },
      ],
    },
  ];

  const optionClicked = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);

    } else {
      setFinalResults(true);
    }
  };
  const restartGame=()=>{
    setScore(0);
    setCurrentQuestion(0);
    setFinalResults(0);
  }


  return (
    <div className="App">
      <h1>QUIZ</h1>


      <h2>შენი ქულა: {score}</h2>

      {showFinalResults ? (
        <div className="finalresults">
          <h1>საბოლოო შედეგები</h1>
          <h2>{score} სწორი პასუხი {questions.length}-დან = ({(score/questions.length)*100}%) </h2>
          <button onClick={()=> restartGame()}>RESTART</button>
        </div>
      ) : (
        <div className="questioncard">
          <h2>
            კითხვა {currentQuestion + 1}    
          </h2>
          <h3 className="questiontext">{questions[currentQuestion].text}</h3>

          <ul>
            {questions[currentQuestion].options.map((option) => {
              return (
                <li
                  onClick={() => optionClicked(option.isCorrect)}
                  key={option.id}
                >
                  {option.text}
                </li>
              );
            })}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
